CREATE TABLE tbl_content (    ContentId uniqueidentifier NOT NULL PRIMARY KEY,    CourseId uniqueidentifier NOT NULL FOREIGN KEY(CourseId) REFERENCES tbl_course(CourseId),    Type varchar(255),    Title nvarchar(255),    Description nvarchar(255),    Datetime datetime,    FileName nvarchar(255),    url nvarchar(255),    CreatedOn datetime,    CreatedBy uniqueidentifier);